import React, { Component } from 'react'

const ScoreboardContext = React.createContext();

export class Provider extends Component {
      state = {
    players: [
      {
        name: "Guil",
        score: 0,
        id: 1
      },
      {
        name: "Treasure",
        score: 0,
        id: 2
      },
      {
        name: "Ashley",
        score: 0,
        id: 3
      },
      {
        name: "James",
        score: 0,
        id: 4
      }
    ]
  };
    render() {
        return (
            ScoreboardContext.Provider
        );
    }
} 
export const Consumer = ScoreboardContext.Consumer;
